import React, {useReducer} from 'react';
import {useRouter} from 'next/router';
import seleccionContext from './seleccionContext';
import seleccionReducer from './seleccionReducer';
import { NUEVA_ETAPA, NUEVA_V_PAIS, NUEVA_V_TIPO, VISIBILIDAD_CARRITO, VACIAR_CARRITO, CAMBIAR_CANTIDAD, AGREGAR_NUEVO } from '../../types/index';

const SeleccionState = ({children}) => {
    const router = useRouter();

    const stateInicial = {
        etapa: null,
        v_pais: null,
        v_tipo: null,
        carrito: false,
        productosCarrito: [
            {
                id: 1,
                nombre: 'Empanada de queso',
                categoria: 'comidas',
                subcategoria: 'mano',
                cantidad: 3
            },
            {
                id: 2,
                nombre: 'Empanada de cebolla',
                categoria: 'comidas',
                subcategoria: 'mano',
                cantidad: 1
            },
            {
                id: 3,
                nombre: 'Empanada de atun',
                categoria: 'comidas',
                subcategoria: 'mano',
                cantidad: 1
            },
            {
                id: 4,
                nombre: 'Empanada de carne',
                categoria: 'comidas',
                subcategoria: 'mano',
                cantidad: 5
            },
            {
                id: 5,
                nombre: 'Catena Zapata XIID',
                categoria: 'vinos',
                subcategoria: 'Espumoso',
                cantidad: 1
            },
            {
                id: 6,
                nombre: 'Coca Cola Zero',
                categoria: 'bebidas',
                subcategoria: 'con-gas',
                cantidad: 2
            },
            {
                id: 7,
                nombre: 'Agua Sin Gas',
                categoria: 'bebidas',
                subcategoria: 'sin-gas',
                cantidad: 1
            },
            {
                id: 8,
                nombre: 'Catena Zapata XIID',
                categoria: 'vinos',
                subcategoria: 'Espumoso',
                cantidad: 1
            }

        ]
    }

    const [state, dispatch] = useReducer(seleccionReducer, stateInicial);

    const cambiarSeleccion = (llave, valor) => {
        if (llave === 'etapa') {
            dispatch({
                type: NUEVA_ETAPA,
                payload: valor
            })
        } else if (llave === 'v_tipo') {
            dispatch({
                type: NUEVA_V_TIPO,
                payload: valor
            })
        } else if (llave === 'v_pais') {
            dispatch({
                type: NUEVA_V_PAIS,
                payload: valor
            })
        }
    }

    const visibilidadCarrito = (estado) => {
        dispatch({
            type: VISIBILIDAD_CARRITO,
            payload: !estado
        })
    }

    const vaciarCarrito = () => {
        dispatch({
            type: VACIAR_CARRITO
        })
    }

    const cambiarCantidad = (id, operacion) => {
        const info = {id, operacion}
        dispatch({
            type: CAMBIAR_CANTIDAD,
            payload: info
        })
    }

    const agregarNuevo = (info) => {
        dispatch({
            type: AGREGAR_NUEVO,
            payload: info
        })
    }

    return (
        <seleccionContext.Provider
            value={{
                etapa: state.etapa,
                v_pais: state.v_pais,
                v_tipo: state.v_tipo,
                carrito: state.carrito,
                productosCarrito: state.productosCarrito,
                cambiarSeleccion,
                visibilidadCarrito,
                vaciarCarrito,
                cambiarCantidad,
                agregarNuevo
            }}
        >
            {children}
        </seleccionContext.Provider>
    )
}

export default SeleccionState;
