import React, {useState, useContext} from 'react';
import styled from '@emotion/styled';
import seleccionContext from '../context/seleccion/seleccionContext';

const Logo = styled.img`

`;

const Lista = styled.ul`
  li {
    padding: 1rem 0;
    border-bottom: 1px solid #7a7a7a;
    text-transform: uppercase;
    span {
      color: #7a7a7a;
    }
  }
  li:last-of-type{
    border-bottom: 1px solid transparent;
  }
`;

const ListaSubItems = styled.ul`
  li {
    border: none;
    padding: 0;
    text-transform: none;
  }
`;

const Inicio = () => {

  const SeleccionContext = useContext(seleccionContext);
  const { cambiarSeleccion, etapa } = SeleccionContext;

  return (
    <div style={{width: '60%', margin: 'auto'}}>
      <Logo className="my-5" src="img/logo.png" alt="Logo Anchoita" />
      <Lista>
        <li><span>01. </span>
          <a>Comida</a>
        </li>
        <li><span>02. </span>
          <a onClick={etapa === 'vinos' ? () => cambiarSeleccion('etapa', null) : () => cambiarSeleccion('etapa', 'vinos')}>
            Vinos
          </a>
          {
            etapa === 'vinos' ? (
              <ListaSubItems>
                <li>
                  <a>
                    Por copa
                  </a>
                </li>
                <li>
                  <a>
                    Argentina
                  </a>
                </li>
                <li>
                  <a>
                    Del mundo
                  </a>
                </li>
              </ListaSubItems>
            ) : null
          }
        </li>

        <li><span>03. </span>
          <a>
            Coctelería
          </a>
        </li>
        <li><span>04. </span>
          <a>
            Bebidas
          </a>
        </li>
      </Lista>
    </div>
  );
}

export default Inicio;
