import React, {useEffect, useContext, useState} from 'react';
import styled from '@emotion/styled';
import seleccionContext from '../context/seleccion/seleccionContext';
import {Row, Col} from 'reactstrap';

const Fondo = styled.div`
    margin: 0 auto;
    width: 95%;
    max-width: 35rem;
    height: 80%;
    background-color: white;
    -webkit-box-shadow: 0px 0px 300px 49px rgba(0,0,0,1);
    -moz-box-shadow: 0px 0px 300px 49px rgba(0,0,0,1);
    box-shadow: 0px 0px 300px 49px rgba(0,0,0,1);
    position: fixed;
    margin: 0 auto;
    top: 50%;
    bottom: 0;
    right: 0;
    left: 0;
    transform: translate(0, -50%);
    transition: all .3s ease;
    padding: 2rem 1.5rem;
`;

const Cerrar = styled.button`
    background-color: transparent;
    border: none;
    font-weight: bold;
    text-align: center;
    margin: 0;
    transition: all .3s ease;
    &:hover {
        transform: rotate(360deg);
    }
    &:focus {
        outline: none;
    }
`;

const Categoria = styled.h5`
    text-transform: uppercase;
    color: var(--colorAzul);
    font-weight: bold;
    font-size: 2rem;
`;

const Nombre = styled.p`
    color: var(--colorAzul);
    margin: 0;
`;

const Cantidad = styled.p`
    color: var(--colorAzul);
    margin: auto .8rem;
    font-weight: bold;
    font-size: 2rem;

`;

const ColumnaCantidad = styled(Col)`
    display: flex;
`;

const BtnSimbolo = styled.button`
    margin: auto;
    border: none;
    background-color: transparent;
    padding: .2rem;
    &:focus {
        outline: none;
    }
`;


const Carrito = () => {

    const SeleccionContext = useContext(seleccionContext);
    const { carrito, visibilidadCarrito } = SeleccionContext;

    const [cats, setCats] = useState({
        comida: false,
        bebidas: false,
        vinos: false,
        cocteles: false
    })

    const carritoProductos = [
        {
            id: 1,
            nombre: 'Empanada de queso',
            categoria: 'comidas',
            subcategoria: 'mano',
            cantidad: 3
        },
        {
            id: 2,
            nombre: 'Empanada de cebolla',
            categoria: 'comidas',
            subcategoria: 'mano',
            cantidad: 1
        },
        {
            id: 3,
            nombre: 'Empanada de atun',
            categoria: 'comidas',
            subcategoria: 'mano',
            cantidad: 1
        },
        {
            id: 4,
            nombre: 'Empanada de carne',
            categoria: 'comidas',
            subcategoria: 'mano',
            cantidad: 5
        },
        {
            id: 5,
            nombre: 'Catena Zapata XIID',
            categoria: 'vinos',
            subcategoria: 'Espumoso',
            cantidad: 1
        },
        {
            id: 6,
            nombre: 'Coca Cola Zero',
            categoria: 'bebidas',
            subcategoria: 'con-gas',
            cantidad: 2
        },
        {
            id: 7,
            nombre: 'Agua Sin Gas',
            categoria: 'bebidas',
            subcategoria: 'sin-gas',
            cantidad: 1
        }
    ]

    return (
        <Fondo id="carrito">
            <div style={{marginBottom: '1rem'}}>
                <Cerrar
                    onClick={() => visibilidadCarrito(carrito)}
                >X</Cerrar>
            </div>
            {
                carritoProductos.map(item => {
                    if (item.categoria === 'comidas') {
                        console.log(cats.comida);
                        if(cats.comida == false) {
                            return (
                                <Categoria>Comidas</Categoria>
                            )
                        }
                        return (
                            <Row key={item.id} style={{marginBottom: '.5rem'}}>
                                <Col xs={8} style={{margin: 'auto'}}>
                                    <Nombre>{item.nombre}</Nombre>
                                </Col>
                                <ColumnaCantidad xs={4} >
                                    <BtnSimbolo>
                                        <img src="img/menos.png" alt="Restar una unidad" />
                                    </BtnSimbolo>
                                    <Cantidad>{item.cantidad}</Cantidad>
                                    <BtnSimbolo>
                                        <img src="img/mas.png" alt="Agregar una unidad" />
                                    </BtnSimbolo>
                                </ColumnaCantidad>
                            </Row>
                        )
                    }
                    
                })
            }
        </Fondo>
    );
}
 
export default Carrito;
