import {Row, Col, Container, NavItem} from 'reactstrap';
import styled from '@emotion/styled';

const Logo = styled.img`

`;

const Lista = styled.ul`
  li {
    padding: 1rem 0;
    border-bottom: 1px solid #7a7a7a;
    text-transform: uppercase;
    span {
      color: #7a7a7a;
    }
  }
  li:last-of-type{
    border-bottom: 1px solid transparent;
  }
`;

const ListaSubItems = styled.ul`
  li {
    border: none;
    padding: 0 2rem;
  }
`;

const Inicio = () => {

  const [seleccion, setSeleccion] = useState(null);

  return (
    <div style={{width: '60%', margin: 'auto'}}>
      <Logo className="my-5" src="img/logo.jpg" alt="Logo" />
      <Lista>
        <li><span>01. </span>
          <a>Comida</a>
        </li>
        <li><span>02. </span>
          <a onClick={seleccion === 'vinos' ? () => setSeleccion(null) : () => setSeleccion('vinos')}>
            Vinos
          </a>
          {
            seleccion === 'vinos' ? (
              <ListaSubItems>
                <li>A</li>
                <li>A</li>
                <li>A</li>
                <li>A</li>
              </ListaSubItems>
            ) : null
          }
        </li>

        <li><span>03. </span>
          <a>
            Cocteler√≠a
          </a>
        </li>
        <li><span>04. </span>
          <a>
            Bebidas
          </a>
        </li>
      </Lista>
    </div>
  );
}

export default Inicio;
