import React from 'react';
import styled from '@emotion/styled';
import {Row, Col} from 'reactstrap';

const Vino = styled(Row)`
    padding: 0 1rem;
    color: var(--colorAzul);
`;

const Nombre = styled.p`
    font-weight: bold;
    font-size: 1.4rem;
    margin: 0;
`;

const Vinedo = styled.p`
    margin: 0;

`;

const Precio = styled.p``;

const AgregarBtn = styled.button`
    background-color: transparent;
    font-weight: bold;
    border: none;
    color: var(--colorAzul);
    transition: all .3s ease;
    text-align: center;
    font-size: 2.5rem;
    margin: auto;
    &:hover{
        color: var(--colorNaranja);
    }
    &:focus {
        outline: none;
    }
`;

const VinoIndividual = () => {
    return (
        <Vino>
            <Col xs={1}>
                <AgregarBtn
                
                >+</AgregarBtn>
            </Col>
            <Col xs={9}>
                <Nombre>Laborum de Parcela Finca Alto Los Culses 2017</Nombre>
                <Vinedo>El Porvenir de Cafayate, Cafayate</Vinedo>
            </Col>
            <Col xs={2}>
                <Precio>145</Precio>
            </Col>
        </Vino>
    );
}
 
export default VinoIndividual;
