import React, {useEffect, useContext} from 'react';
import styled from '@emotion/styled';
import seleccionContext from '../context/seleccion/seleccionContext';
import {Row, Col} from 'reactstrap';

const Fondo = styled.div`
    margin: 0 auto;
    width: 95%;
    max-width: 35rem;
    height: 80%;
    background-color: white;
    -webkit-box-shadow: 0px 0px 300px 49px rgba(0,0,0,1);
    -moz-box-shadow: 0px 0px 300px 49px rgba(0,0,0,1);
    box-shadow: 0px 0px 300px 49px rgba(0,0,0,1);
    position: fixed;
    margin: 0 auto;
    top: 50%;
    bottom: 0;
    right: 0;
    left: 0;
    transform: translate(0, -50%);
    transition: all .3s ease;
    padding: 2rem 1.5rem;
`;

const Cerrar = styled.button`
    background-color: transparent;
    border: none;
    font-weight: bold;
    text-align: center;
    margin: 0;
    transition: all .3s ease;
    &:hover {
        transform: rotate(360deg);
    }
    &:focus {
        outline: none;
    }
`;

const Categoria = styled.h5`
    text-transform: uppercase;
    color: var(--colorAzul);
    font-weight: bold;
    font-size: 2rem;
`;

const Item = styled.div`

`;

const Nombre = styled.p`
    color: var(--colorAzul);

`;


const Cantidad = styled.p`
    color: var(--colorAzul);
    margin: 0 .5rem;
`;

const ColumnaCantidad = styled(Col)`
    display: flex;
`;

const BtnSimbolo = styled.button`
    border: 0;
    background-color: var(--colorAzul);
    padding: .5rem;
    border-radius: 100%;
    color: white;
    font-weight: bold;
    height: 2.5rem;
    width: 2.5rem;
    &:focus {
        outline: none;
    }
`;


const Carrito = () => {

    const SeleccionContext = useContext(seleccionContext);
    const { carrito, visibilidadCarrito } = SeleccionContext;

    return (
        <Fondo id="carrito">
            <div style={{marginBottom: '1rem'}}>
                <Cerrar
                    onClick={() => visibilidadCarrito(carrito)}
                >X</Cerrar>
            </div>
            <Categoria>Comida</Categoria>
            <Row>
                <Col
                    xs={9}
                >
                    <Nombre>Empanada de queso</Nombre>
                </Col>
                <ColumnaCantidad
                    xs={3}
                >
                    <BtnSimbolo>-</BtnSimbolo>
                    <Cantidad>4</Cantidad>
                    <BtnSimbolo>+</BtnSimbolo>
                </ColumnaCantidad>
            </Row>
        </Fondo>
    );
}
 
export default Carrito;
