import {
    NUEVA_ETAPA, NUEVA_V_PAIS, NUEVA_V_TIPO, VISIBILIDAD_CARRITO, VACIAR_CARRITO, CAMBIAR_CANTIDAD, AGREGAR_NUEVO
} from '../../types/index';

const Reducer = (state, action) => {

    switch(action.type) {
                
        case NUEVA_ETAPA:
            return {
                ...state,
                etapa: action.payload,
                v_tipo: null,
                v_pais: null
            }

        case NUEVA_V_PAIS:
        return {
            ...state,
            v_pais: action.payload,
            v_tipo: null
        }

        case NUEVA_V_TIPO:
            return {
                ...state,
                v_tipo: action.payload,
                v_pais: null
            }

        case VISIBILIDAD_CARRITO: 
            return {
                ...state,
                carrito: action.payload
            }

        case VACIAR_CARRITO:
            return {
                ...state,
                productosCarrito: []
            }

        case AGREGAR_NUEVO:
            const nuevo = {
                id: productosCarrito.length + 1,
                nombre: action.payload.nombre,
                categoria: action.payload.categoria,
                subcategoria: action.payload.subcategoria,
                cantidad: 1
            }
            return {
                ...state,
                productosCarrito: productosCarrito.push(nuevo)
            }

        case CAMBIAR_CANTIDAD:
            const carr = state.productosCarrito.map(prod => {
                if(prod.id === action.payload.id) {
                    prod.cantidad ++;
                    return prod;
                } else {
                    return prod;
                }
            })

            return {
                ...state,
                productosCarrito: carr
            }

        default:
            return state;
    }
}
 
export default Reducer;
