import React, {useEffect, useContext, useState} from 'react';
import styled from '@emotion/styled';
import seleccionContext from '../context/seleccion/seleccionContext';
import {Row, Col} from 'reactstrap';
import ItemCarrito from './ItemCarrito';

const Fondo = styled.div`
    margin: 0 auto;
    width: 95%;
    max-width: 35rem;
    height: 80%;
    background-color: white;
    -webkit-box-shadow: 0px 0px 300px 49px rgba(0,0,0,1);
    -moz-box-shadow: 0px 0px 300px 49px rgba(0,0,0,1);
    box-shadow: 0px 0px 300px 49px rgba(0,0,0,1);
    position: fixed;
    margin: 0 auto;
    top: 50%;
    bottom: 0;
    right: 0;
    left: 0;
    transform: translate(0, -50%);
    transition: all .3s ease;
    padding: 2rem 1.5rem;
    color: var(--colorAzul);
`;

const Cerrar = styled.button`
    background-color: transparent;
    border: none;
    font-weight: bold;
    text-align: center;
    margin: 0;
    transition: all .3s ease;
    &:hover {
        transform: rotate(360deg);
    }
    &:focus {
        outline: none;
    }
`;

const Categoria = styled.h5`
    text-transform: uppercase;
    color: var(--colorAzul);
    font-weight: bold;
    font-size: 2rem;
    padding-top: 1rem;
`;

const Carrito = () => {

    const SeleccionContext = useContext(seleccionContext);
    const { carrito, visibilidadCarrito } = SeleccionContext;

    const [cats, setCats] = useState({
        comida: false,
        bebidas: false,
        vinos: false,
        cocteles: false
    })

    const carritoProductos = [
        {       
            categoria: 'Comida',
            productos: [
                {
                    id: 1,
                    nombre: 'Empanada de queso',
                    subcategoria: 'mano',
                    cantidad: 3
                },
                {
                    id: 2,
                    nombre: 'Empanada de cebolla',
                    subcategoria: 'mano',
                    cantidad: 1
                },
                {
                    id: 3,
                    nombre: 'Empanada de atun',
                    subcategoria: 'mano',
                    cantidad: 1
                },
                {
                    id: 4,
                    nombre: 'Empanada de carne',
                    subcategoria: 'mano',
                    cantidad: 5
                }
            ]
        },
        {       
            categoria: 'Vino',
            productos: [
                {
                    id: 5,
                    nombre: 'Catena Zapata XIID',
                    categoria: 'vinos',
                    subcategoria: 'Espumoso',
                    cantidad: 1
                },
            ]
        },
        {       
            categoria: 'C贸cteles',
            productos: [

            ]
        },
        {       
            categoria: 'Bebidas',
            productos: [
                {
                    id: 6,
                    nombre: 'Coca Cola Zero',
                    categoria: 'bebidas',
                    subcategoria: 'con-gas',
                    cantidad: 2
                },
                {
                    id: 7,
                    nombre: 'Agua Sin Gas',
                    categoria: 'bebidas',
                    subcategoria: 'sin-gas',
                    cantidad: 1
                }
            ]
        }  
    ]

    return (
        <Fondo id="carrito">
            <div style={{marginBottom: '1rem'}}>
                <Cerrar
                    onClick={() => visibilidadCarrito(carrito)}
                >X</Cerrar>
            </div>
            {
                (carritoProductos[0].productos.length === 0) ? null : (
                    <Categoria>Comidas</Categoria>,
                    carritoProductos[0].productos.map(item => (
                        <ItemCarrito item={item} />
                    ))
                )
            }
            <Categoria>Vinos</Categoria>
            {
                (carritoProductos[1].productos.length === 0) ? <p>No hay vinos seleccionados</p> : (
                    carritoProductos[1].productos.map(item => (
                        <ItemCarrito item={item} />
                    ))
                )
            }
            <Categoria>C贸cteles</Categoria>
            {
                (carritoProductos[2].productos.length === 0) ? <p>No hay c贸cteles seleccionados</p> : (
                    carritoProductos[2].productos.map(item => (
                        <ItemCarrito item={item} />
                    ))
                )
            }
            <Categoria>Bebidas</Categoria>
            {
                (carritoProductos[3].productos.length === 0) ? <p>No hay bebidas seleccionadas</p> : (
                    carritoProductos[3].productos.map(item => (
                        <ItemCarrito item={item} />
                    ))
                )
            }
        </Fondo>
    );
}
 
export default Carrito;
