import React, {useEffect, useState} from 'react';
import styled from '@emotion/styled';

const Blur = styled.div`
    -webkit-filter: blur(5px);
    -moz-filter: blur(5px);
    -o-filter: blur(5px);
    -ms-filter: blur(5px);
    filter: blur(5px);
    position: absolute;
`;

const Fondo = styled.div`
    margin: 0 auto;
    width: 95%;
    max-width: 35rem;
    height: 80%;
    background-color: white;
    -webkit-box-shadow: -7px 7px 20px -2px rgba(0,0,0,0.56);
    -moz-box-shadow: -7px 7px 20px -2px rgba(0,0,0,0.56);
    box-shadow: -7px 7px 20px -2px rgba(0,0,0,0.56);
    position: fixed;
    margin: 0 auto;
    top: 50%;
    bottom: 0;
    right: 0;
    left: 0;
    transform: translate(0, -50%);
    transition: all .3s ease;
`;

const Cerrar = styled.button`
    background-color: transparent;
    border: none;
    font-weight: bold;
    text-align: center;
    margin: 0;
    padding: 1rem;
    transition: all .3s ease;
    &:hover {
        transform: rotate(360deg);
    }
    &:focus {
        outline: none;
    }
`;

const Carrito = () => {
    return (
        <Blur>
            <Fondo>
                <div>
                    <Cerrar>X</Cerrar>
                </div>
            </Fondo>
        </Blur>
    );
}
 
export default Carrito;
