import React, {useReducer} from 'react';
import {useRouter} from 'next/router';
import seleccionContext from './seleccionContext';
import seleccionReducer from './seleccionReducer';
import { NUEVA_ETAPA, NUEVA_V_PAIS, NUEVA_V_TIPO, VISIBILIDAD_CARRITO } from '../../types/index';

const SeleccionState = ({children}) => {
    const router = useRouter();

    const stateInicial = {
        etapa: null,
        v_pais: null,
        v_tipo: null,
        carrito: false
    }

    const [state, dispatch] = useReducer(seleccionReducer, stateInicial);

    const cambiarSeleccion = (llave, valor) => {
        if (llave === 'etapa') {
            dispatch({
                type: NUEVA_ETAPA,
                payload: valor
            })
        } else if (llave === 'v_tipo') {
            dispatch({
                type: NUEVA_V_TIPO,
                payload: valor
            })
        } else if (llave === 'v_pais') {
            dispatch({
                type: NUEVA_V_PAIS,
                payload: valor
            })
        }
    }

    const visibilidadCarrito = () => {
        dispatch({
            type: VISIBILIDAD_CARRITO,
            payload: !carrito
        })
    }

    return (
        <seleccionContext.Provider
            value={{
                etapa: state.etapa,
                v_pais: state.v_pais,
                v_tipo: state.v_tipo,
                carrito: state.carrito,
                cambiarSeleccion,
                visibilidadCarrito
            }}
        >
            {children}
        </seleccionContext.Provider>
    )
}

export default SeleccionState;
