import React from 'react';
import styled from '@emotion/styled';
import {Row, Col} from 'reactstrap';

const Fondo = styled.div`
    background-color: white;
    padding: 3rem;
`;

const ItemTipo = styled.button`
    background-color: transparent;
    border: none;
    color: var(--colorAzul);
    font-weight: bold;
    padding: 1rem 0;
    border-bottom: 1px solid var(--colorAzul);
    transition: all .3s ease;
    &:focus {
        outline: none;
    }
`;

const EleccionContenido = () => {
    return (
        <Fondo>
            <Row className="text-center">
                <Col xs={6} className="mx-auto my-3">
                    <ItemTipo

                    >Espumoso</ItemTipo>
                </Col>
                <Col xs={6} className="mx-auto my-3">
                    <ItemTipo

                    >Blanco</ItemTipo>
                </Col>
                <Col xs={6} className="mx-auto my-3">
                    <ItemTipo

                    >Naranjo</ItemTipo>
                </Col>
                <Col xs={6} className="mx-auto my-3">
                    <ItemTipo

                    >Rosado</ItemTipo>
                </Col>
                <Col xs={6} className="mx-auto my-3">
                    <ItemTipo

                    >Tinto</ItemTipo>
                </Col>
                <Col xs={6} className="mx-auto my-3">
                    <ItemTipo

                    >Dulce</ItemTipo>
                </Col>
                <Col xs={6} className="mx-auto my-3">
                    <ItemTipo

                    >Fortificado seco</ItemTipo>
                </Col>
                <Col xs={6} className="mx-auto my-3">
                    <ItemTipo

                    >Fortificado dulce</ItemTipo>
                </Col>
                <Col xs={6} className="mx-auto my-3">
                    <ItemTipo

                    >Otros tama√±os</ItemTipo>
                </Col>

                
            </Row>
        </Fondo>
    );
}
 
export default EleccionContenido;
