import React from 'react';
import {Row, Col} from 'reactstrap';
import styled from '@emotion/styled';

const Nombre = styled.p`
    color: var(--colorAzul);
    margin: 0;
`;

const Cantidad = styled.p`
    color: var(--colorAzul);
    margin: auto .8rem;
    font-weight: bold;
    font-size: 2rem;

`;

const ColumnaCantidad = styled(Col)`
    display: flex;
`;

const BtnSimbolo = styled.button`
    margin: auto;
    border: none;
    background-color: transparent;
    padding: .2rem;
    &:focus {
        outline: none;
    }
`;


const ItemCarrito = ({item}) => {
    return (
        <Row key={item.id} style={{marginBottom: '.5rem'}}>
            <Col xs={8} style={{margin: 'auto'}}>
                <Nombre>{item.nombre}</Nombre>
            </Col>
            <ColumnaCantidad xs={4} >
                <BtnSimbolo>
                    <img src="img/menos.png" alt="Restar una unidad" />
                </BtnSimbolo>
                <Cantidad>{item.cantidad}</Cantidad>
                <BtnSimbolo>
                    <img src="img/mas.png" alt="Agregar una unidad" />
                </BtnSimbolo>
            </ColumnaCantidad>
        </Row>
    );
}
 
export default ItemCarrito;
