import React, {useEffect} from 'react';
import styled from '@emotion/styled';
import ItemIndividual from './ItemIndividual';

const Fondo = styled.div`
    background-color: white;
    padding: 4rem 0;
    position: relative;
`;
const VolverBtn = styled.button`
    color: var(--colorAzul);
    position: absolute;
    top: 5px;
    left: 5px;
    border: none;
    font-size: 1.2rem;
    font-weight: bold;
    padding: .1rem 1rem;
    background-color: #cacaca;  
    transition: all .3s ease;
    &:hover {
        background-color: #f0f0f0;
    }
    &:focus {
        outline: none;
    }
`;
const Provincia = styled.p`
    text-transform: uppercase;
    color: var(--colorNaranja);
    padding-left: 3rem;
    font-size: 1.8rem;
    margin-bottom: .5rem;
`;
const Uva = styled.p`
    color: black;
    padding-left: 4.2rem;
    font-weight: bold;
    font-size: 1.2rem;
    margin: 0 0 1rem 0;
    &:before {
        content: '•';
        padding-right: .5rem;
        vertical-align: text-bottom;
    }
`;

const VinosContenido = ({contenido, tipo, pais, categorias, etapa}) => {
    
    useEffect(() => {
        window.location.href = `#${tipo}`;
        // eslint-disable-next-line
    }, [])

    const regiones = [
        { es: 'Borgoña', en: 'Burgundy' },
        { es: 'Burdeos', en: 'Bordeaux' },
        { es: 'Champagne', en: 'Champagne' },
        { es: 'Toscana', en: 'Tuscany' },
        { es: 'Piemonte', en: '' },
        { es: 'Mosel', en: '' },
        { es: 'Malborough', en: '' },
        { es: 'Montsant', en: '' },
        { es: 'Penedés', en: '' },
        { es: 'Rias Baixas', en: '' },
        { es: 'Valdeorras', en: '' },
        { es: 'Montilla - Moriles', en: '' },
        { es: 'Jerez de la Frontera', en: '' },
        { es: 'Sanlúcar de Barrameda', en: '' },
        { es: 'Tenerife', en: '' },
        { es: 'Priorat', en: '' },
        { es: 'Ribera del Duero', en: '' },
        { es: 'Sierra de Gredos', en: '' },
        { es: 'Elqui', en: '' },
        { es: 'Itata', en: '' },
        { es: 'Maipo', en: '' },
        { es: 'Maule', en: '' },
        { es: 'Madeira', en: '' },
        { es: 'Oporto', en: '' },
        { es: 'Valle de Uco', en: '' },
        { es: 'Luján de Cuyo', en: '' },
        { es: 'Valles Calchaquíes', en: '' },
        { es: 'Trevelin', en: '' },
    ]

    const provincias = [
        { es: 'Buenos Aires', en: 'Buenos Aires' },
        { es: 'Catamarca', en: 'Catamarca' },
        { es: 'Chubut', en: 'Chubut' },
        { es: 'Córdoba', en: 'Córdoba' },
        { es: 'Jujuy', en: 'Jujuy' },
        { es: 'La Rioja', en: 'La Rioja' },
        { es: 'Mendoza', en: 'Mendoza' },
        { es: 'Río Negro', en: 'Río Negro' },
        { es: 'Salta', en: 'Salta' },
        { es: 'San Juan', en: 'San Juan' },
    ]

    return (
        <Fondo>
            {/* <VolverBtn>VOLVER</VolverBtn> */}

            {/* <Provincia>Salta</Provincia> */}
            {/* <Uva>Chardonnay</Uva> */}

            {
                categorias.map((item, i) => (
                    <div key={item.codigo}>
                        <Provincia  id={item.codigo}>{item.nombre}</Provincia>
                        {
                            contenido.map(prod => (
                                (prod.tipo === item.codigo) ? (
                                    (pais === 'argentina' && prod.pais === 'Argentina') ? (
                                        <ItemIndividual key={prod.id} producto={prod} etapa={etapa} />
                                    ) : (pais === 'mundo' && prod.pais !== 'Argentina') ? (
                                        <ItemIndividual key={prod.id} producto={prod} etapa={etapa} />
                                    ) : (pais === 'copa' && prod.copa === true) ? (
                                        <ItemIndividual key={prod.id} producto={prod} etapa={etapa} />
                                    ) : null

                                ) : null
                            ))
                        }
                    </div>       
                ))
            }
        </Fondo>
    );
}
 
export default VinosContenido;
