import React from 'react';
import {Row, Col} from 'reactstrap';
import styled from '@emotion/styled';
import VinoIndividual from './VinoIndividual';

const Fondo = styled.div`
    background-color: white;
    padding: 4rem 0;
    position: relative;
`;

const VolverBtn = styled.button`
    color: var(--colorAzul);
    position: absolute;
    top: 5px;
    left: 5px;
    border: none;
    font-size: 1.2rem;
    font-weight: bold;
    padding: .1rem 1rem;
    background-color: #cacaca;  
    transition: all .3s ease;
    &:hover {
        background-color: #f0f0f0;
    }
    &:focus {
        outline: none;
    }
`;

const Provincia = styled.p`
    text-transform: uppercase;
    color: var(--colorNaranja);
    padding-left: 3rem;
    font-size: 1.8rem;
    margin-bottom: .5rem;
`;

const Uva = styled.p`
    color: black;
    padding-left: 4.2rem;
    font-weight: bold;
    font-size: 1.2rem;
    margin: 0 0 1rem 0;
    &:before {
        content: 'â€¢';
        padding-right: .5rem;
        vertical-align: text-bottom;
    }
`;

const MenuContenido = () => {

    const vinoEjemplo = {
        id: 1,
        nombre: 'Laborum de Parcela Finca Alto Los Culses 2017',
        precio: 145,
        vinedo: 'El Porvenir de Cafayate, Cafayate',
        e_nombre: 'ENG Laborum de Parcela',
        e_vinedo: 'The Porvenir of Cafayate, Cafayate',
        region: 'Salta',
        pais: 'Argentina',
        uva: 'Chardonnay'
    }

    return (
        <Fondo>
            <VolverBtn
                
            >VOLVER</VolverBtn>

            <Provincia>Salta</Provincia>
            <Uva>Chardonnay</Uva>
            <VinoIndividual />
            <VinoIndividual />
            <VinoIndividual />
        </Fondo>
    );
}
 
export default MenuContenido;
