import React, {useContext} from 'react';
import styled from '@emotion/styled';
import {Row, Col} from 'reactstrap';
import seleccionContext from '../context/seleccion/seleccionContext';

const Fondo = styled.div`
    background-color: white;
    padding: 3rem;
`;

const ItemTipo = styled.button`
    background-color: transparent;
    border: none;
    color: var(--colorAzul);
    font-weight: bold;
    padding: 1rem 0;
    border-bottom: 1px solid var(--colorAzul);
    transition: all .3s ease;
    &:focus {
        outline: none;
    }
`;

const EleccionContenido = () => {
    const SeleccionContext = useContext(seleccionContext);
    const { cambiarSeleccion, etapa, v_pais, v_tipo } = SeleccionContext;

    if (etapa === 'vinos' && v_pais !== null) {
        return (
            <Fondo>
                <Row className="text-center">
                    <Col xs={6} className="mx-auto my-3">
                        <ItemTipo
                            onClick={() => cambiarSeleccion('v_tipo', 'espumoso')}
                        >Espumoso</ItemTipo>
                    </Col>
                    <Col xs={6} className="mx-auto my-3">
                        <ItemTipo
                            onClick={() => cambiarSeleccion('v_tipo', 'blanco')}
                        >Blanco</ItemTipo>
                    </Col>
                    <Col xs={6} className="mx-auto my-3">
                        <ItemTipo
                            onClick={() => cambiarSeleccion('v_tipo', 'naranjo')}    
                        >Naranjo</ItemTipo>
                    </Col>
                    <Col xs={6} className="mx-auto my-3">
                        <ItemTipo
                            onClick={() => cambiarSeleccion('v_tipo', 'rosado')}    
                        >Rosado</ItemTipo>
                    </Col>
                    <Col xs={6} className="mx-auto my-3">
                        <ItemTipo
                            onClick={() => cambiarSeleccion('v_tipo', 'tinto')}    
                        >Tinto</ItemTipo>
                    </Col>
                    <Col xs={6} className="mx-auto my-3">
                        <ItemTipo
                            onClick={() => cambiarSeleccion('v_tipo', 'dulce')}    
                        >Dulce</ItemTipo>
                    </Col>
                    <Col xs={6} className="mx-auto my-3">
                        <ItemTipo
                            onClick={() => cambiarSeleccion('v_tipo', 'fortificado_seco')}    
                        >Fortificado seco</ItemTipo>
                    </Col>
                    <Col xs={6} className="mx-auto my-3">
                        <ItemTipo
                            onClick={() => cambiarSeleccion('v_tipo', 'fortificado_dulce')}    
                        >Fortificado dulce</ItemTipo>
                    </Col>
                    <Col xs={6} className="mx-auto my-3">
                        <ItemTipo
                            onClick={() => cambiarSeleccion('v_tipo', 'tamanos')}    
                        >Otros tamaños</ItemTipo>
                    </Col>                
                </Row>
            </Fondo>
        );
    } else {
        return null;
    }
}
 
export default EleccionContenido;
