import React, {useState} from 'react';
import styled from '@emotion/styled';

const Nav = styled.div`
    display: inline-block;
`;

const Item = styled.button`
    background-color: transparent;
    border: none;
    color: white;
    text-transform: uppercase;
    transition: all .3s ease;
    &:hover {
        color: var(--colorNaranja);
    }
    &:focus {
        outline: none;
    }
`;

const NavSecundario = styled(Nav)`
    border-top: 1px solid #7a7a7a;
    padding-top: 1rem;
    margin-top: 1rem;
`;

const ItemSec = styled(Item)`
    /* padding: 0 1rem;
    border-right: 1px solid #7a7a7a;
    line-height: 1.5rem;
    &:last-of-type {
        border-right: none;
    } */
`;

const NavegacionMenu = () => {
    
    const [seleccion, setSeleccion] = useState(null);
    const [seleccionSec, setSeleccionSec] = useState(null);

    return (
        <div className="py-5 mx-auto text-center">
            <Nav>
                <Item
                    onClick={() => setSeleccion('comidas')}
                    className={seleccion === 'comidas' ? 'activo' : null}
                >Comidas</Item>
                <Item
                    onClick={() => setSeleccion('vinos')}
                    className={seleccion === 'vinos' ? 'activo' : null}
                >Vinos</Item>
                <Item
                    onClick={() => setSeleccion('cocteleria')}
                    className={seleccion === 'cocteleria' ? 'activo' : null}
                >Cocteler√≠a</Item>
                <Item
                    onClick={() => setSeleccion('bebidas')}
                    className={seleccion === 'bebidas' ? 'activo' : null}
                >Bebidas</Item>
            </Nav>

            {
                seleccion === 'vinos' ? (
                    <NavSecundario>
                        <Item
                            onClick={() => setSeleccionSec('copa')}
                            className={seleccionSec === 'copa' ? 'activo' : null}
                        >Por copa</Item>
                        <Item
                            onClick={() => setSeleccionSec('argentina')}
                            className={seleccionSec === 'argentina' ? 'activo' : null}
                        >Argentina</Item>
                        <Item
                            onClick={() => setSeleccionSec('mundo')}
                            className={seleccionSec === 'mundo' ? 'activo' : null}
                        >Del mundo</Item>
                    </NavSecundario>
                ) : null
            }
        </div>
    );
}
 
export default NavegacionMenu;
